ow = mysqli_fetch_assoc($result); $data[] = $row);		$result = '';		$adress='';		foreach ($data as $elem) {									$adress .= 	$elem['adres_dostavki'];														}		$sqlForNameProd = "SELECT `name` FROM `in stock` WHERE `id_product` = $idProd";		$result = mysqli_query($link, $sqlForNameProd ) or die(mysqli_error($link));		for ($data = []; $row = mysqli_fetch_assoc($result); $data[] = $row);		$result = '';		$NameProd ='';		foreach ($data as $elem) {								$NameProd .= 	$elem['name'];											}		$sqlFinal = "INSERT INTO `product_order` (`id_customer`, `ShippingAddress`, `id_product`, `product_name`, `amount`) VALUES ('$userID','$adress','$idProd','$NameProd','$amount')";	$result = mysqli_query($link, $sqlFinal ) or die(mysqli_error($link));	return $result;		}?>